'use strict'

const Sequelize = require('sequelize')
const Op = Sequelize.Op

module.exports = function setupGb (GbModel, DateModel, ClassModel, RegionModel, ClassModel) {
  async function createOrUpdate (traffic, dateA, type, regionA) {
    const fecha = await DateModel.findOne({
      where: { fecha: dateA }
    })

    if (!fecha) {
      return 1
    }

    const tipo = await ClassModel.findOne({
      where: { tipo: type }
    })

    if (!tipo) {
      return 2
    }

    const region = await RegionModel.findOne({
      where: { region: regionA }
    })

    if (!region) {
      return 3
    }

    Object.assign(traffic, { fechaId: fecha.id, tipoId: tipo.id, regionId: region.id })
    const cond = {
      where: {
        fechaId: traffic.fechaId,
        tipoId: traffic.tipoId,
        regionId: traffic.regionId,
        estado: traffic.estado
      }
    }
    const existingTraffic = await GbModel.findOne(cond)

    if (existingTraffic) {
      const updated = await GbModel.update(traffic, cond)
      return updated ? GbModel.findOne(cond) : existingTraffic
    }

    const result = await GbModel.create(traffic)
    return result.toJSON()
  }

  function findAll () {
    return GbModel.findAll({
      attributes: ['id', 'trafico', 'estado'],
      group: ['id'],
      include: [
        {
          attributes: ['fecha'],
          model: DateModel
        },
        {
          attributes: ['tipo'],
          model: ClassModel
        },
        {
          attributes: ['region'],
          model: RegionModel
        }
      ],
      raw: true
    })
  }

  async function findByRange (startDate, finalDate, tipo, estado) {
    return GbModel.findAll({
      attributes: ['trafico'],
      where: {
        estado
      },
      include: [
        {
          attributes: ['fecha'],
          model: DateModel,
          where: {
            fecha: {
              [Op.and]: {
                [Op.gte]: startDate,
                [Op.lte]: finalDate
              }
            }
          }
        },
        {
          attributes: ['tipo'],
          model: ClassModel,
          where: {
            tipo
          }
        }
      ],
      raw: true
    })
  }

  return {
    createOrUpdate,
    findAll,
    findByRange
  }
}
