'use strict'

const Sequelize = require('sequelize')
const Op = Sequelize.Op

module.exports = function setupUsers (UsersModel, DateModel, TypeModel, ClassModel) {
  async function createOrUpdate (users, dateA, type, clase) {
    const fecha = await DateModel.findOne({
      where: { fecha: dateA }
    })

    if (!fecha) {
      return 1
    }

    const tipo = await TypeModel.findOne({
      where: { tipo: type }
    })

    if (!tipo) {
      return 2
    }

    const clase = await ClassModel.findOne({
      where: { clase }
    })

    if (!clase) {
      return 3
    }

    Object.assign(users, { fechaId: fecha.id, tipoId: tipo.id, claseId: clase.id })
    const cond = {
      where: {
        fechaId: users.fechaId,
        tipoId: users.tipoId,
        claseId: users.claseId,
        estado: users.estado
      }
    }
    const existingUsers = await UsersModel.findOne(cond)

    if (existingUsers) {
      const updated = await UsersModel.update(users, cond)
      return updated ? UsersModel.findOne(cond) : existingUsers
    }

    const result = await UsersModel.create(users)
    return result.toJSON()
  }

  function findAll () {
    return UsersModel.findAll()
  }

  async function findByRange (startDate, finalDate, tipo, clase, estado) {
    return UsersModel.findAll({
      attributes: ['usuarios'],
      where: {
        estado
      },
      include: [
        {
          attributes: ['fecha'],
          model: DateModel,
          where: {
            fecha: {
              [Op.and]: {
                [Op.gte]: startDate.format('YYYY-MM-DD'),
                [Op.lte]: finalDate.format('YYYY-MM-DD')
              }
            }
          }
        },
        {
          attributes: ['tipo'],
          model: TypeModel,
          where: {
            tipo
          }
        },
        {
          attributes: ['clase'],
          model: ClassModel,
          where: {
            clase
          }
        }
      ]
    })
  }

  return {
    createOrUpdate,
    findAll,
    findByRange
  }
}
